import React, { useState } from 'react';

export default function AssetTable() {
  const [playerName, setPlayerName] = useState("");
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");

  const fetchData = async () => {
    setError("");
    setResults(null);

    try {
      const url = `http://localhost:7071/api/getassetsbyplayer?playerName=${playerName}`;
      const resp = await fetch(url);
      const data = await resp.json();
      if (resp.ok) {
        setResults(data);
      } else {
        setError(data?.error || "Error");
      }
    } catch (e) {
      console.error(e);
      setError(e.message);
    }
  };

  return (
    <div>
      <div>
        <label>Nhập PlayerName: </label>
        <input
          type="text"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
        />
        <button onClick={fetchData}>Lấy danh sách Asset</button>
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {results && (
        <div style={{ marginTop: '20px' }}>
          <h3>
            Player: {results.player.PlayerName} (ID: {results.player.PlayerId})
          </h3>
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>Asset Name</th>
                <th>Level Require</th>
              </tr>
            </thead>
            <tbody>
              {results.assets.map((a, idx) => (
                <tr key={idx}>
                  <td>{a.AssetName}</td>
                  <td>{a.LevelRequire}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
