import React, { useState } from 'react';

export default function CreateAssetForm() {
  const [AssetName, setAssetName] = useState('');
  const [LevelRequire, setLevelRequire] = useState('');
  const [OwnerPlayerName, setOwnerPlayerName] = useState('');
  const [msg, setMsg] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setMsg('');
    fetch('http://localhost:7071/api/createasset', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        AssetName,
        LevelRequire,
        OwnerPlayerName
      })
    })
      .then(res => res.json())
      .then(data => {
        if (data.asset) {
          setMsg('Tạo Asset thành công!');
          setAssetName('');
          setLevelRequire('');
          setOwnerPlayerName('');
        } else {
          setMsg(JSON.stringify(data));
        }
      })
      .catch(err => setMsg(err.message));
  };

  return (
    <div style={{ border: '1px solid gray', padding: '10px', marginTop: '20px' }}>
      <h3>Tạo Asset</h3>
      {msg && <p style={{ color: 'red' }}>{msg}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>AssetName: </label>
          <input
            type="text"
            value={AssetName}
            onChange={e => setAssetName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>LevelRequire: </label>
          <input
            type="number"
            value={LevelRequire}
            onChange={e => setLevelRequire(e.target.value)}
          />
        </div>
        <div>
          <label>OwnerPlayerName (tuỳ chọn): </label>
          <input
            type="text"
            value={OwnerPlayerName}
            onChange={e => setOwnerPlayerName(e.target.value)}
          />
        </div>
        <button type="submit">Tạo</button>
      </form>
    </div>
  );
}
